const initialState={
	myFavorites:[],
	myFavoritesCopy:[]
	};

export default function rootReducer(state=initialState, action){
	switch (action.type){
	case 'ADD_FAVORITE':
	return {
		...state,
		myFavoritesCopy:[...state.myFavoritesCopy, action.payload],
		myFavorites:[...state.myFavoritesCopy, action.payload]
		};
	case 'DELETE_FAVORITE':
	//const myFavorites=[...state.myFavorites];
	//const myFavoritesRender=state.myFavorites.filter((ch)=>ch.id !== action.payload)

	//const myFavoritesCopy=[...state.myFavoritesCopy]
	const myFavoritesNotRender = state.myFavoritesCopy.filter((ch)=>ch.id !== action.payload)
	return {
		...state,
		myFavorites:myFavoritesNotRender,
		myFavoritesCopy:myFavoritesNotRender
		};
	case 'Filter':
	//const myFavoritesCopy=[...state.myFavoritesCopy];
	const filter=state.myFavoritesCopy.filter((ch)=>ch.gender === action.payload)
	return {
		...state,
		myFavorites:filter
		}
	default:
	return state;
		}
	}
