import './App.css'
import React from 'react'
import Cards from './components/Cards.jsx'
import Nav from './components/Nav.jsx'
function App () {
const [data, setCharacters]=React.useState({
	characters:[],
	arr:[],
	})

function agregar(){
        setCharacters({
        ...data,
        characters:[{name: 'Morty Smith',
   species: 'Human',
   gender: 'Male',
   image: 'https://rickandmortyapi.com/api/character/avatar/2.jpeg'
}],
        })
        }

  return (
<div className='App'>
	<div>
	  <Nav agregar={agregar}/>
	</div>
      <hr/>
      <div>
        <Cards
          characters={data.characters}
        />
      </div>
</div>
  );
}

export default App
