const initialState={
	myFavorites:[]
	};

export default function rootReducer(state=initialState, action){
	switch (action.type){
	case 'ADD_FAVORITE':
	return {
		myFavorites:[...state.myFavorites]
		};
	case 'DELETE_FAVORITE':
	return {
		myFavorites:state.myFavorites.filter((ch)=>ch.id !== action.payload)
		};
	default:
	return state;
		}
	}
