import React,{useState,useEffect} from 'react'
import styles from './Style.module.css'
import {Link} from 'react-router-dom'
import {addCharacter,deleteCharacter} from '../redux/actions.js'

export function Card(props) {
	const [isFav,setIsFav]=useState(false);
	useEffect(() => {
   		props.myFavorites.forEach((fav) => {
      		if (fav.id === props.id) {
         	setIsFav(true);
      		}
   	});
	}, [myFavorites]);
	function handleFavorite(){
	if(isFav){
		setIsFav(!isFav);
		deleteCharacter(props.id)
		}
	if(!isFav){
		setIsFav(!isFav);
		addCharacter(props)
		}
	}

   return (
      <div className={styles.cards}>
	{
 	  isFav ? (
      <button onClick={handleFavorite}>‚ù§Ô∏è</button>
       ) : (
      <button onClick={handleFavorite}>ü§ç</button>
        )
    }
        <button className={styles.btnClose} value={props.id} onClick={()=>props.onClose(props.id)}><b>X</b></button>
	<ul className={styles.data}>
		<li><h2><b>Name:</b><Link to={`/detail/${props.id}`}>{props.name}</Link></h2></li>
		<li><h2><b>Species:</b>{props.species}</h2></li>
		<li><h2><b>Gender:</b>{props.gender}</h2></li>
	</ul>
        <img  src={props.image} alt="Rick Sanchez" /> 
      </div>
   );
}
function mapDispatchToProps(dispatch){
	return{
		add_Favorite:function(character){
			dispatch(addCharacter(character))
			},
		delete_Favorite:function(id){
			dispatch(deleteCharacter(id))
			}
		}
	}
function mapStateToProps(state){
	return {
		myFavorites:state.myFavorites
		}
	}
export default connect(mapStateToProps,mapDispatchToProps)(Card)
