import styles from './Style.module.css';
import {Link} from 'react-router-dom';
import {addCharacter,deleteCharacter} from '../redux/actions.js';
import {connect} from 'react-redux';
import React from 'react';


export function Card({ name, species, gender, image, id, onClose, addCharacter, deleteCharacter, myFavorites }) {
	const [isFav,setIsFav]=React.useState(false)

React.useEffect(() => {
   myFavorites.forEach((fav) => {
      if (fav.id === id) {
         setIsFav(true);
      }
   });
}, [myFavorites]);


	function handleFavorite(){
	if(isFav){
		setIsFav(false);
		deleteCharacter(id)
		}
	if (!isFav){
		setIsFav(true);
		addCharacter({name, species, gender, image, id})
		}
	}



   return (
      <div className={styles.cards}>
	{
   isFav ? (
      <button onClick={handleFavorite}>‚ù§Ô∏è</button>
   ) : (
      <button onClick={handleFavorite}>ü§ç</button>
   )
}
        <button className={styles.btnClose} value={id} onClick={()=>onClose(id)}><b>X</b></button>
	<ul className={styles.data}>
		<li><h2><b>Name:</b><Link to={`/detail/${id}`}>{name}</Link></h2></li>
		<li><h2><b>Species:</b>{species}</h2></li>
		<li><h2><b>Gender:</b>{gender}</h2></li>
	</ul>
        <img  src={image} alt="Rick Sanchez" /> 
      </div>
   );
}
function mapDispatchToProps(dispatch){
	return {
		addCharacter:function(props){
			dispatch(addCharacter(props))
			},
		deleteCharacter:function(id){
                        dispatch(deleteCharacter(id))
                        }
		}
	}
function mapStateToProps(state){
	return {
		myFavorites:state.myfavorites
		}
	}
export default connect(mapStateToProps,mapDispatchToProps)(Card)
