import React from 'react'
import styles from './Form.module.css'
import validate from './validation.js'

export default function Form(){
	const [userData,setUserData]=React.useState({
        username:'',
        password:''
        });
	const [errors,setErrors]=React.useState({});
	function handleInputChange(e){
		setUserData({
		...userData,
		[e.target.name]:e.target.value
		})
		setErrors(validate({
		...userData,
		[e.target.name]:e.target.value
		}))
	}
	return (<div>
	<label>Username</label><br/><input name='username' value={userData.username} onChange={(e)=>handleInputChange(e)}/>
	<label>Password</label><br/><input name='password' value={userData.password} onChange={(e)=>handleInputChange(e)}/>
	{}
	<button>Login</button>
	</div>)
	}
