import styles from './Style.module.css';
import {Link} from 'react-router-dom';
import {addFavorite,deleteFavorite} from '../redux/actions.js';
import {connect} from 'react-redux';
import React from 'react';
import {useDispatch,useSelector} from 'react-redux'

export function Card(props) {
	const [isFav,setIsFav]=React.useState(false);
	const dispatch = useDispatch();

React.useEffect(()=>{
	for (let i=0;i<=props.myFavorites;i++){
		if (props.myFavorites[i].id===props.id){
			setIsFav(true)
			}
		}
	},[props.myFavorites])

	function handleFavorite(props){
	if(isFav){
		setIsFav(false);
		props.deleteFavorite(props.id)
		}else{
		setIsFav(true);
		props.addFavorite(props)
		}
	}



   return (
      <div className={styles.cards}>
	{
   isFav ? <button onClick={()=>handleFavorite(props.id)}>true</button> : <button onClick={()=>handleFavorite(props)}>false</button>
}
        <button className={styles.btnClose} value={props.id} onClick={()=>props.onClose(props.id)}><b>X</b></button>
	<ul className={styles.data}>
		<li><h2><b>Name:</b><Link to={`/detail/${props.id}`}>{props.name}</Link></h2></li>
		<li><h2><b>Species:</b>{props.species}</h2></li>
		<li><h2><b>Gender:</b>{props.gender}</h2></li>
	</ul>
        <img  src={props.image} alt={props.name} /> 
      </div>
   );
}
