const initialState={
	myFavorites:[]
	}

export default function rootReducer(state=initialState, action){
	switch (action.type){
	case 'ADD_FAVORITE':
	return {
		...state,
		myFavorites:[state.myFavorites,action.payload]
		}
	case 'DELETE_FAVORITE':
	return {
		...state,
		myFavorites:state.myFavorite.filter((ch)=>ch.id !== action.payload)
		}
	default:
	return state;
		}
	}
