	}, [props.myFavorites]);

	function handleFavorite(){
	if(isFav){
		setIsFav(!isFav);
		deleteCharacter(props.id)
		}
	if(!isFav){
		setIsFav(!isFav);
		addCharacter(props)
		}
	}
