import './App.css'
import {useState,useLocation,useEffect} from 'react'
import {Routes,Route} from "react-router-dom"
import Cards from './components/Cards.jsx'
import Nav from './components/Nav.jsx'
import About from './components/About.jsx'
import Detail from "./components/Detail.jsx"
import Form from "./components/Form.jsx"


function App () {
const [characters, setCharacters]=useState([]);
const [show,setShow]=useState(true);
const location=useLocation();

	useEffect(()=>{
	if (location.pathname==="/"){
		setShow(false)
		}else{
		setShow(true)
		}
	},[location])

function onSearch(character){
        fetch('https://rickandmortyapi.com/api/character/'+character)
      .then((response) => response.json())
      .then((data) => {
         if (data.name) {
	let exists = characters.find((e)=> e.id === data.id)
	if(exists){
	window.alert("Ya existe")
	}else{
	setCharacters((oldChars) => [...oldChars, data]);
	}
         } else {
            window.alert('No hay personajes con ese ID');
         }
      })
        }
function randomChar(){
	fetch('https://rickandmortyapi.com/api/character/'+Math.floor((Math.random()*825)+1))
      .then((response) => response.json())
      .then((data) => {
         if (data.name) {
            setCharacters((oldChars) => [...oldChars, data]);
         } else {
            window.alert('No hay personajes con ese ID');
         }
      });
	}




function onClose(id){
	//setCharacters(allCharacters.filter((ch)=>ch.id === e.target.value))
	setCharacters(characters.filter((ch)=>ch.id !== id))
	//console.log("ola");
}



  return (
<div className='App'>
	<div>
	{show && <Nav onSearch={onSearch} randomChar={randomChar}/>}
	</div>
      <hr/>
	<Routes>
	  <Route path="/" element={<Form/>}></Route>
	  <Route path="/home" element={<Cards characters={characters} onClose={onClose}/>}></Route>
	  <Route path="/about" element={<About />}></Route>
	  <Route path="/detail/:detailId" element={<Detail />}></Route>
	</Routes>
</div>
  );
}

export default App
