import {useState,useEffect} from "react"
import {useParams} from 'react-router-dom';
import style from "./Nothome.module.css"

export default function Detail(){
	const {detailId}=useParams();
	const [character, setCharacter]=useState({});

	useEffect(() => {
  fetch(`https://rickandmortyapi.com/api/character/${detailId}`)
    .then((response) => response.json())
    .then((char) => {
      if (char.name) {
        setCharacter(char);
      } else {
        window.alert("No hay personajes con ese ID");
      }
    })
    .catch((err) => {
      window.alert("No hay personajes con ese ID");
    });
  return setCharacter({});
}, [detailId]);

	return (<div className={style.detail}>
		<img src={character.image} alt={character.name} className={style.img}/>
		<h2><b>Name:</b>{character.name}</h2>
		<h2><b>Status:</b>{character.status}</h2>
		<h2><b>Specie:</b>{character.species}</h2>
		<h2><b>Gender:</b>{character.gender}</h2>
		<h2><b>Origin:</b>{character.origin?.name}</h2>
		</div>)
	}
