import './App.css'
import {useState} from 'react'
import Cards from './components/Cards.jsx'
import Nav from './components/Nav.jsx'
function App () {
const [characters, setCharacters]=useState([])

function onSearch(character){
        fetch('https://rickandmortyapi.com/api/character/${character}')
      .then((response) => response.json())
      .then((data) => {
         if (data.name) {
	let exists = characters.find((e)=> e.id === data.id)
	if(exists){
	window.alert("Ya existe")
	}else{
	setCharacters((oldChars) => [...oldChars, data]);
	}
         } else {
            window.alert('No hay personajes con ese ID');
         }
      })
        }
function randomChar(){
	fetch(`https://rickandmortyapi.com/api/character/${Math.floor(Math.random()*825+1)}`)
      .then((response) => response.json())
      .then((data) => {
         if (data.name) {
            setCharacters((oldChars) => [...oldChars, data]);
         } else {
            window.alert('No hay personajes con ese ID');
         }
      });
	}




function onClose(id){
	//setCharacters(allCharacters.filter((ch)=>ch.id === e.target.value))
	setCharacters(characters.filter((ch)=>ch.id !== id))
	//console.log("ola");
}
  return (
<div className='App'>
	<div>
	  <Nav onSearch={onSearch}/>
	</div>
      <hr/>
      <div>
        <Cards
          characters={characters} onClose={onClose}
        />
      </div>
</div>
  );
}

export default App
