import styles from './Style.module.css';
import {Link} from 'react-router-dom';
import {addFavorite,deleteFavorite} from '../redux/actions.js';
import React from 'react';
import {useDispatch,useSelector} from 'react-redux'

export default function Card(props) {
	const [isFav,setIsFav]=React.useState(false);
	const dispatch = useDispatch();
	const myFavorites = useSelector((s) => s.myFavorites);


React.useEffect(() => {
	if(myFavorites){
   for (let i=0; i<myFavorites.length; i++){
      if (myFavorites[i].id === props.id) {
         setIsFav(true);
      }
   }
}
}, [myFavorites]);

	function handleFavorite(props){
	if(isFav){
		setIsFav(false);
		dispatch(deleteFavorite(props.id))
		}else{
		setIsFav(true);
		dispatch(addFavorite(props))
		}
	}



   return (
      <div>
	<div className={styles.cards}>


	<div>
	<img  src={props.image} alt={props.name} />
	<ul className={styles.data}>
		<li><h2><b>Name:</b><Link to={`/detail/${props.id}`}>{props.name}</Link></h2></li>
		<li><h2><b>Species:</b>{props.species}</h2></li>
		<li><h2><b>Gender:</b>{props.gender}</h2></li>
	</ul>
	</div> 


	<button className={styles.btnClose} value={props.id} onClick={()=>props.onClose(props.id)}><b>X</b></button>
	{
   isFav ? (
      <button className={styles.btnClose} onClick={()=>handleFavorite(props)}>❤️</button>
   ) : (
      <button className={styles.btnClose} onClick={()=>handleFavorite(props)}>�</button>
   )
}

	</div>
      </div>
   );
}
