const initialState={
	myFavorites:[]
	};

export default function rootReducer(state=initialState, action){
	switch (action.type){
	case 'ADD_FAVORITE':
	return {
		...state,
		myFavorites:[...state.myFavorites, action.payload]
		};
	case 'DELETE_FAVORITE':
	const filtered=state.myFavorites.filter((id)=>id !== action.payload);
	return {
		...state,
		myFavorites:filtered
		};
	default:
	return state;
		}
	}
