		{
                props.characters.map((ch)=>{
                        return(
                        <Card name={ch.name} species={ch.species} gender={ch.gender} image={ch.image} id={ch.id} onClose={props.onClose}/>
                        );
                        })
                }
		</div>)
	}
function mapStateToProps(state){
	return{
		myFavorites:state.myFavorites
		}
	}
