import React from 'react'
import styles from './Form.module.css'
import Validate from './validation.js'

export default function Form(){
	const [userData,setUserData]=React.useState({
        username:'',
        password:''
        });
	const [errors,setErrors]=React.useState({
		email:'',
		password:''
	});
	function handleInputChange(e){
		setUserData({
		...userData,
		[e.target.name]:e.target.value
		})
		setErrors(Validate({
		...userData,
		[e.target.name]:e.target.value
		}))
	}
	return (<div className={styles.Login}>
	<form>
	<label className={styles.label}>Username</label><br/><input  type='text' name='username' value={userData.username} onChange={(e)=>handleInputChange(e)}/>
	<p className={styles.error}>{errors.email && errors.email}</p>
	<label className={styles.label}>Password</label><br/><input type='password' name='password' value={userData.password} onChange={(e)=>handleInputChange(e)}/>
	<p className={styles.error}>{errors.password && errors.password}</p>
	<button type='submit'>Login</button>
	</form>
	</div>)
	}
